import React from 'react'

const MenuCard = ({ menuData }) => {
    // console.log(menuData);
    return (
        <>
            <section className='main-card'></section>
            {menuData.map((curElm) => {
                return (
                    <>
                        <div className="card-container">               
                            <div className="card">                   
                                <div className="card-body">
                                    <span className="card-number card-circle subtle">1</span>
                                    <span className="card-author subtle">Breakfast</span>
                                    <h2 className="card-title">Maggi</h2>
                                    <span>
                                        Maggi is a brick of instant noodles that you crack in
                                        half over boiling water, then cook with a flavored
                                        powder that looks like straight-up turmeric and tastes
                                        like 2,000 percent of your daily recommended sodium
                                        intake. Maggi 2-Minute Noodle is a phenomenon.
                                    </span>
                                    <div className="card-read">Read</div>
                                </div>
                                {/* <img src={image} alt="images" className="card-media" /> */}
                                <span className="card-tag subtle">Order Now</span>               
                            </div>
                        </div>
                    </>
                )
            })}           
        </>
    );
};

export default MenuCard;
