import "./style.css";

const reducer = (state,action) => {
  if (action.type === "INCR")
    state = state + 1;
};
export const UseReducer = () => {
  // const [myNum, setmyNum]=React.useState(0);
  const initialData = 0;
  const [state,dispatch] =useReducer(reducer,initialData);

  return (
    <>
      <div className='center_div'>
        <p>{state}</p>
        <div class="button2" onClick={()=>dispatch()}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          INCR
        </div>
